column.value:
context.loc_id = input_row.id;

//System.out.println("SQL-String: " + input_row.teilenummer +", "+ input_row.b_index);
if (input_row.teilenummer != null && input_row.t_index != null) {
    context.loc_varSQLString = "WITH Hierarchical ( baugruppe, b_index, teilenummer, t_index, position, menge, variante, auswahlmenge, benennung, t_kategorie, cdb_status_txt, werkstoff, techdaten, mengeneinheit, STATUS, t_bereich, cdbxml_level, sortOrder ) AS ( SELECT et.baugruppe, et.b_index, et.teilenummer, et.t_index, et.position, et.menge, et.variante, et.auswahlmenge, ts.benennung, ts.t_kategorie, ts.cdb_status_txt, ts.werkstoff, ts.techdaten, ts.mengeneinheit, ts.STATUS, ts.t_bereich, 0 AS cdbxml_level, cast(position AS VARBINARY(900)) AS sortOrder FROM einzelteile AS et INNER JOIN teile_stamm AS ts ON et.teilenummer = ts.teilenummer AND et.t_index = ts.t_index WHERE et.baugruppe = '" + input_row.teilenummer + "' AND et.b_index = '" + input_row.t_index + "' UNION ALL SELECT et.baugruppe, et.b_index, et.teilenummer, et.t_index, et.position, et.menge, et.variante, et.auswahlmenge, ts.benennung, ts.t_kategorie, ts.cdb_status_txt, ts.werkstoff, ts.techdaten, ts.mengeneinheit, ts.STATUS, ts.t_bereich, cdbxml_level + 1, cast(h.sortOrder + cast(et.position AS VARBINARY(16)) AS VARBINARY(900)) FROM einzelteile AS et INNER JOIN teile_stamm AS ts ON et.teilenummer = ts.teilenummer AND et.t_index = ts.t_index INNER JOIN Hierarchical AS h ON h.teilenummer = et.baugruppe AND h.t_index = et.b_index WHERE 1 > 0 AND cdbxml_level < 10 ) SELECT baugruppe,b_index, teilenummer, t_index FROM Hierarchical";

    //		"select teilenummer, t_index from einzelteile_v where b_index = '"+input_row.b_index+"' and baugruppe = '" + input_row.teilenummer +"' ORDER BY position ASC";

    output_row.teilenummer = input_row.teilenummer;
    output_row.t_index = input_row.t_index;
    output_row.baugruppe = input_row.teilenummer;
    output_row.b_index = input_row.t_index;
    context.loc_varPartnumber = input_row.teilenummer;
    context.loc_varB_Index = input_row.t_index;
} else if (input_row.t_index == null) {
    context.loc_varSQLString = "WITH Hierarchical ( baugruppe, b_index, teilenummer, t_index, position, menge, variante, auswahlmenge, benennung, t_kategorie, cdb_status_txt, werkstoff, techdaten, mengeneinheit, STATUS, t_bereich, cdbxml_level, sortOrder ) AS ( SELECT et.baugruppe, et.b_index, et.teilenummer, et.t_index, et.position, et.menge, et.variante, et.auswahlmenge, ts.benennung, ts.t_kategorie, ts.cdb_status_txt, ts.werkstoff, ts.techdaten, ts.mengeneinheit, ts.STATUS, ts.t_bereich, 0 AS cdbxml_level, cast(position AS VARBINARY(900)) AS sortOrder FROM einzelteile AS et INNER JOIN teile_stamm AS ts ON et.teilenummer = ts.teilenummer AND et.t_index = ts.t_index WHERE et.baugruppe = '" + input_row.teilenummer + "' AND et.b_index = '' UNION ALL SELECT et.baugruppe, et.b_index, et.teilenummer, et.t_index, et.position, et.menge, et.variante, et.auswahlmenge, ts.benennung, ts.t_kategorie, ts.cdb_status_txt, ts.werkstoff, ts.techdaten, ts.mengeneinheit, ts.STATUS, ts.t_bereich, cdbxml_level + 1, cast(h.sortOrder + cast(et.position AS VARBINARY(16)) AS VARBINARY(900)) FROM einzelteile AS et INNER JOIN teile_stamm AS ts ON et.teilenummer = ts.teilenummer AND et.t_index = ts.t_index INNER JOIN Hierarchical AS h ON h.teilenummer = et.baugruppe AND h.t_index = et.b_index WHERE 1 > 0 AND cdbxml_level < 10 ) SELECT baugruppe,b_index, teilenummer, t_index FROM Hierarchical";

