//change all XML-TAGs in UpperCase

//System.out.println("Das Ergebnis ist: " + input_row.body);

try {
   DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
   factory.setValidating(false);
   factory.setNamespaceAware(true);

   Document document = factory.newDocumentBuilder().parse(new InputSource(new StringReader(input_row.body.replaceAll("[\\r\\n]", ""))));

   Pattern pattern = Pattern.compile("<(/?)([^>]+)>");
   Matcher matcher = pattern.matcher(input_row.body);
   StringBuffer str_output = new StringBuffer();

   while (matcher.find()) {
      String tag = matcher.group(0);
      String convertedTag = tag.toUpperCase();
      matcher.appendReplacement(str_output, convertedTag);
   }

   matcher.appendTail(str_output);
   context.loc_body = str_output.toString();
   context.loc_error="";
   context.loc_xslt = "<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"><xsl:output method=\"xml\" indent=\"yes\"/><!-- Template to format the date --><xsl:template name=\"formatDate\"><xsl:param name=\"date\"/><xsl:variable name=\"month\"><xsl:choose><xsl:when test=\"substring($date, 1, 3) = 'Jan'\">01</xsl:when><xsl:when test=\"substring($date, 1, 3) = 'Feb'\">02</xsl:when><xsl:when test=\"substring($date, 1, 3) = 'Mar'\">03</xsl:when><xsl:when test=\"substring($date, 1, 3) = 'Apr'\">04</xsl:when><xsl:when test=\"substring($date, 1, 3) = 'May'\">05</xsl:when><xsl:when test=\"substring($date, 1, 3) = 'Jun'\">06</xsl:when><xsl:when test=\"substring($date, 1, 3) = 'Jul'\">07</xsl:when><xsl:when test=\"substring($date, 1, 3) = 'Aug'\">08</xsl:when><xsl:when test=\"substring($date, 1, 3) = 'Sep'\">09</xsl:when><xsl:when test=\"substring($date, 1, 3) = 'Oct'\">10</xsl:when><xsl:when test=\"substring($date, 1, 3) = 'Nov'\">11</xsl:when><xsl:when test=\"substring($date, 1, 3) = 'Dec'\">12</xsl:when></xsl:choose></xsl:variable><xsl:variable name=\"day\" select=\"format-number(substring($date, 5, 2), '00')\"/><xsl:variable name=\"year\" select=\"substring($date, 8, 4)\"/><xsl:value-of select=\"concat($year, $month, $day)\"/></xsl:template><!-- Match child elements of ROW --><xsl:template match=\"ROW/*\"><!-- Create DATA element --><DATA><FIELDNAME><xsl:value-of select=\"name()\"/></FIELDNAME><VALUE_CHAR><!-- Check if FIELDNAME is \"cdb_mdate\" --><xsl:if test=\"name() = 'CDB_MDATE' and string-length(.) > 8\"><!-- Call the formatDate template to format the date --><xsl:call-template name=\"formatDate\"><xsl:with-param name=\"date\" select=\".\"/></xsl:call-template></xsl:if><!-- For all other cases, keep VALUE_CHAR as is --><xsl:if test=\"name() = 'CDB_MDATE' and string-length(.) = 8\"><xsl:value-of select=\".\"/></xsl:if><xsl:if test=\"name() != 'CDB_MDATE'\"><xsl:value-of select=\".\"/></xsl:if></VALUE_CHAR></DATA></xsl:template><!-- Match the root element --><xsl:template match=\"ROOT\"><ROOT><!-- Create CDB element --><CDB><!-- Get the TEILENUMMER value --><xsl:variable name=\"teilenummer\" select=\"ROW/TEILENUMMER\"/><!-- Place TEILENUMMER under CDB --><TEILENUMMER><xsl:value-of select=\"$teilenummer\"/></TEILENUMMER><!-- Apply templates for each ROW element child --><xsl:apply-templates select=\"ROW/*[not(self::TEILENUMMER)]\"/></CDB></ROOT></xsl:template></xsl:stylesheet>";

   //"<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"><xsl:output method=\"xml\" indent=\"yes\"/><!-- Match the root element --><xsl:template match=\"ROOT\"><ROOT><!-- Create CDB element --><CDB><!-- Get the TEILENUMMER value --><xsl:variable name=\"teilenummer\" select=\"ROW/TEILENUMMER\"/><!-- Place TEILENUMMER under CDB --><TEILENUMMER><xsl:value-of select=\"$teilenummer\"/></TEILENUMMER><!-- Apply templates for each ROW element child --><xsl:apply-templates select=\"ROW/*[not(self::TEILENUMMER)]\"/></CDB></ROOT></xsl:template><!-- Match child elements of ROW --><xsl:template match=\"ROW/*\"><!-- Create DATA element --><DATA><FIELDNAME><xsl:value-of select=\"name()\"/></FIELDNAME><VALUE_CHAR><xsl:value-of select=\".\"/></VALUE_CHAR></DATA></xsl:template></xsl:stylesheet>";

} catch (Exception e) {
   context.loc_body = "";
   context.loc_error=input_row.body;
   context.loc_xslt="";
   //e.printStackTrace();
}

